mapper.select:
select
log_id, user_id, log_intro, log_time, table_status
from log_records


        and log_id = #{logId}


        and user_id = #{userId}


        and log_intro = #{logIntro}


        and log_time = #{logTime}


        and table_status = #{tableStatus}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from log_records


        and log_id = #{logId}


        and user_id = #{userId}


        and log_intro = #{logIntro}


        and log_time = #{logTime}


        and table_status = #{tableStatus}
